package com.grv.linkedlist;

public class LLNode {

	
	int data;
	
	LLNode next;
	public LLNode(int data) {
		// TODO Auto-generated constructor stub
		this.data=data;
		
		
	}
	public int getData() {
		return data;
	}
	public void setData(int data) {
		this.data = data;
	}
	public LLNode getNext() {
		return next;
	}
	public void setNext(LLNode next) {
		this.next = next;
	} 
	
	
}
